import React, { Component } from 'react'
import axios from 'axios'
import 'bootstrap/dist/css/bootstrap.min.css';
import './css/create_schedule.css';

class CreateSchedule extends Component {
	constructor(props){
		super(props);
		this.state = {enablePeriod:false,type:'default'}
	}

	componentWillMount(){

	}

	componentWillUnmount(){

	}

	onChange = (e) => {	
		this.setState({
			[e.target.name] : e.target.value
		})

		if(e.target.name == 'type'){
			if(e.target.value != 'default'){
				this.setState({enablePeriod : true})
			}else{
				this.setState({enablePeriod : false})
			}
		}
	}

	onSubmit = (e) => {
		e.preventDefault()

		var formData = new FormData()

		formData.append('name', this.state.name)
		formData.append('type', this.state.type)
		formData.append('date-from', this.state['period-from'])
		formData.append('date-to', this.state['period-to'])

		axios({
			method : 'post',
			data : formData,
			url : 'http://localhost:8080/scheduler/create',
			headers : {
				'Content-Type' : 'multipart/form-data'
			}
		}).then((response) => {
			if(response.data == 'success'){
				alert('The schedule has been added to the database')
			}else if(response.data == 'default_exists'){
				alert('There is one default schedule in the database, you can only modify or delete it and recreate')
			}else{
				alert('Server not responding')
			}
		}).catch(response => {
			console.log(response)
		})
	}

	render = () => {
		return (
			<div class='create_schedule_div'>
				<form class='create_schedule_form' onSubmit={this.onSubmit}>
					<label for='name'>Schedule Name</label>
					<input type='text' id='name' onChange={this.onChange} name='name' placeholder='Schedule Name'/>

					<label for='type'>Schedule Type</label>
					<select id='type' name='type' onChange={this.onChange}>
						<option value='default' selected>Default</option>
						<option value='short-term'>Short-term</option>
					</select>

					<table id='period-table'>
						<tr>
							<td><label for='period-from'>From</label></td>
							<td><input type='date' onChange={this.onChange} disabled={!this.state.enablePeriod} id='period-from' name='period-from'/></td>
						</tr>

						<tr>
							<td><label for='period-to'>To</label></td>
							<td><input type='date' onChange={this.onChange} disabled={!this.state.enablePeriod} id='period-to' name='period-to'/></td>
						</tr>
					</table>
					<input type='submit' id='submit' value='Create Schedule' class='btn btn-primary'/>
				</form>
			</div>
		)
	}
}

export default CreateSchedule;
